$((function(){JSON.parse(localStorage.getItem("cart")).map(n=>{$(`\n        <div data-id=${n.id} class="item">\n                    <button id="delete">X</button>\n                    <img src=${n.img}\n                        alt="">\n                    <div class="name">${n.name}</div>\n                    <div class="price">\n                        <span class="price-discount">\n                           $${Math.round(n.price-n.price*n.discount/100)}.00</span>\n                    </div>\n                    <form class="flex a-center" action="#">\n                        <div class="btn flex  a-center">\n                            <div class="dec-button">-</div>\n                            <input type="text" class="quantity_" id="quantity" value=${n.quantity}>\n                            <div class="inc-button">+</div>\n                        </div>\n                    </form>\n                    <div class="subtotal">$${Math.round(n.price-n.price*n.discount/100)*n.quantity}.00\n                    </div>\n                    <button class="update" >Update Cart</button>\n                </div>\n                <div class="b-bottom"></div>\n                \n        `).appendTo(".cols")})}));